			recesion: true,
			car1: 'jaguar',
			car2: 'porshe',
		}
	}



	render() {

		return this.state.recesion == true ? <div>I will buy {this.state.car1}</div> :
			<div> I will buy {this.car1} and {this.state.car2}</div>;


