import React from 'react';
import { Component } from 'react';

class Welcome extends Component {


	constructor() {
		super();
		this.state = {
			name: 'Sacramento',
			count: 0
		};
	}

	changeText() {
		this.setState({
			name: 'Toronto',
			count: prevState =>{ prevState.count+1 }
		});
	}

	changeText5() {
		this.changeText();
		this.changeText();
		this.changeText();
		this.changeText();
		this.changeText();

	}

	render() {

		return (
			<div>
				<h1>Welcome {this.props.name} and {this.props.name2}</h1>
				<h2>location: {this.state.name}</h2>
				{this.state.count}
				<button onClick={() => this.changeText5()}>What's next</button>

			</div>);
	}
}

export default Welcome;
